import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
const HomePage = () => {
  const navigate = useNavigate();
  const [authenticated, setAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch user authentication status from the server
    axios
      .get('http://localhost:4000/auth/check-auth', {
        withCredentials: true,
      })
      .then((response) => {
        if (response.data.authenticated) {
          setAuthenticated(true); // If authenticated, set the state to true
        }
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error checking authentication:', error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!authenticated) {
    // Redirect to login if not authenticated
    navigate('/login');
  }

  // If authenticated, render the home page content
  return (
    <div>
      <h1>Welcome to the Home Page!</h1>
      <p>This is the content for authenticated users.</p>
    </div>
  );
};

export default HomePage;
