import React from 'react';
import '../css/productpage.css';
import HomeHeader from '../subpages/HomeHeader';
import sonyWirelessHeadphones from '../images/sonywirelessheadphones.jpeg';
import data from '../data';
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import isAuthenticated from '../functions/IsAuthenticated';
import { WhatsappShareButton } from 'react-share';
import { toast, ToastContainer } from 'react-toastify';
const ProductPage = () => {
  const { slug } = useParams();
  const [product, setProduct] = useState(null);
  const [isClicked, setIsClicked] = useState(false);
  const { username } = isAuthenticated();
  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(
          `http://localhost:4000/api/products/product/${slug}`
        ); // Replace with your API endpoint
        if (response.ok) {
          const data = await response.json();
          setProduct(data);
        }
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    };
    fetchProduct();

    const fetchLikeStatus = async () => {
      try {
        const response = await fetch(
          `http://localhost:4000/api/like/${username}`
        );
        console.log('hi');

        if (response.ok) {
          const data = await response.json();
          setIsClicked(data.status);
          console.log(data.status);
        }
      } catch (error) {
        console.error('Error fetching like status:', error);
      }
    };

    fetchLikeStatus();
  }, [slug, username]);

  const addToCart = async () => {
    try {
      const response = await fetch(
        `http://localhost:4000/user/${username}/cart/add`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            productId: product._id,
            productName: product.name,
          }),
        }
      );

      if (response.ok) {
        toast.success('Added Successfully ', {
          position: 'bottom-right',
        });
      } else {
        toast.error('Failed to Add to Cart', {
          position: 'bottom-right',
        });
      }
    } catch (error) {
      console.error('Error adding product to cart:', error);
    }
  };

  const handleLike = async () => {
    const response = await fetch(`http://localhost:4000/api/like/${username}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        productSlug: product.slug,
      }),
    });

    if (response.ok) {
      const data = await response.json();
      toast.success('Liked Successfully', {
        position: 'bottom-right',
      });

      //change
      console.log('status' + data.status);
      if (data.status) {
        setIsClicked(true);
      } else {
        setIsClicked(false);
      }
    } else {
      toast.error('Failed to Like', {
        position: 'bottom-right',
      });
    }
  };

  const elementStyles = {
    // backgroundColor: isClicked ? 'red' : 'black', // Change color when clicked
    color: isClicked ? 'red' : 'black',
  };

  return (
    <div>
      {product ? (
        <div className="product-container">
          <HomeHeader />
          <div className="product-body">
            <div className="product-left">
              <img
                src={require(`../images/${product.image}`)}
                alt="Headphone"
                className="product-image"
              />
              <div className="product-share">
                <WhatsappShareButton url={window.location.href}>
                  Share on whatsapp
                </WhatsappShareButton>
              </div>
            </div>
            <div className="product-right">
              <div className="product-description"></div>
              <h1>{product.name}</h1>
              <h3>{product.brand}</h3>
              <h3>{product.rating} stars</h3>
              <h3>Price: ₹{product.price}</h3>
              <div className="product-buy">
                <input
                  type="button"
                  className="product-add-to-cart"
                  value="Add to Cart"
                  onClick={addToCart}
                />
                <input
                  type="button"
                  className="product-buy-now"
                  value="Buy Now"
                />
              </div>

              <span className="product-label">Brand Name:</span>
              <span className="product-value">{product.name}</span>
              <br />
              <span className="product-label">Model Name:</span>
              <span className="product-value">{product.name}</span>
              <br />
              <span className="product-label">Color:</span>
              <span className="product-value">{product.color}</span>
              <br />
              <span className="product-label">Description:</span>
              <span className="product-value">{product.description}</span>
              <br />
              <p style={elementStyles} onClick={handleLike}>
                Like
              </p>
            </div>
          </div>
          <ToastContainer />
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ProductPage;
