import React, { useState } from 'react';
import GetUserDetails from '../functions/GetUserDetails';
import Left from '../subpages/Left';
import { Link } from 'react-router-dom';
import '../css/edit.css';
const Home = () => {
  const { userDetails } = GetUserDetails();
  const [isAddStudentClassClicked, setisAddStudentClassClicked] =
    useState(false);

  const handleAddStudentClass = () => {
    setisAddStudentClassClicked(!isAddStudentClassClicked);
  };
  const addStudentClass = isAddStudentClassClicked
    ? 'edit-add-student'
    : 'edit-not-add-student';
  return (
    <>
      <div className="home-home_page">
        {userDetails ? (
          <>
            <div className="home-container">
              <div className="home-dashboard">
                <Left
                  iconBg1=""
                  iconText1=""
                  iconBg2=""
                  iconText2=""
                  iconBg3="#3D3B40"
                  iconText3="white"
                  iconBg4=""
                  iconText4=""
                  iconBg5=""
                  iconText5=""
                />
                <div className="edit-right">
                  <div
                    className={addStudentClass}
                    onClick={handleAddStudentClass}
                  >
                    <p>add a student</p>
                  </div>

                  <div className="edit-add-student">
                    <input type="text" />
                  </div>
                  <p>vs</p>
                </div>
              </div>
            </div>
          </>
        ) : (
          <p>Loading user details...</p>
        )}
      </div>
    </>
  );
};

export default Home;
