import React from 'react';
import HomeHeader from '../subpages/HomeHeader';
import sonyWirelessHeadphones from '../images/sonywirelessheadphones.jpeg';
import '../css/MyCart.css';
import { Link, useNavigate } from 'react-router-dom';
import GetUserDetails from '../functions/GetUserDetails';
import '../css/MyFavourites.css';
const MyFavourites = () => {
  const { userDetails } = GetUserDetails();

  return (
    <>
      {userDetails ? (
        <div className="myfavourites-container">
          <HomeHeader />
          <div className="myfavourites-body">
            <div className="myfavourites-products">
              {userDetails.cart.map((item, index) => (
                <div className="myfavourites-product" key={index}>
                  <img
                    src={require(`../images/${item.image}`)}
                    alt=""
                    className="myfavourites-image"
                  />
                  <div className="myfavourites-product-detail">
                    <p>{item.productName}</p>
                    <p>â‚¹{item.price}</p>
                    <p>Quantity: {item.quantity}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      ) : (
        <p>Loading user details...</p>
      )}
    </>
  );
};

export default MyFavourites;
